"use strict";(self.webpackChunkcodisity_com=self.webpackChunkcodisity_com||[]).push([[4215],{8896:e=>{e.exports=JSON.parse('{"label":"WebSocket","permalink":"/tags/web-socket","allTagsPath":"/tags","count":1,"items":[{"id":"fullstack/websocket/index","title":"WebSocket","description":"WebSocket is a communication protocol that provides full-duplex, bidirectional communication channels over a single, long-lived TCP (Transmission Control Protocol) connection. Unlike the traditional request-response model of HTTP (Hypertext Transfer Protocol), where a client sends a request to a server and waits for a response, WebSocket allows for real-time, low-latency, and continuous communication between a client (typically a web browser) and a server. WebSocket is commonly used for building interactive and dynamic web applications, online games, chat applications, and other real-time applications. Here are key aspects of WebSocket:","permalink":"/fullstack/websocket/"}]}')}}]);